// 定义输入数据
const inputData = $input.all();

const cleanText = (text) => {
  return text
    // 移除图片链接 (包含在方括号中的http链接)
    .replace(/\[http.*?\]/g, '')
    // 将3个以上连续换行符替换为2个换行符
    .replace(/\n{3,}/g, '\n\n')
    // 去除开头和结尾的空白字符
    .trim();
};

// 处理输入数据 - 直接处理对象数组
const processedData = inputData.map(item => ({
  json: {
    data: cleanText(item.json.data)
  }
}));

// 返回处理后的数据
return processedData;
