/**
 * N8N Code节点 - 提取并格式化文章数据
 * 从输入数据中提取title、link和contentsnippet字段，并按指定格式组合
 * @param {Array} items - N8N输入数据数组 ($input.all())
 * @returns {Array} - 返回N8N格式的对象数组
 */
function processData() {
  // 获取输入数据
  const items = $input.all();

  // 检查输入数据是否有效
  if (!Array.isArray(items) || items.length === 0) {
    return [{ json: { output: '' } }];
  }

  // 提取字段并格式化
  const formattedText = items.map(item => {
    const { title = '', link = '', contentsnippet = '' } = item.json || {};
    return `标题：${title}\n链接：${link}\n内容：${contentsnippet}\n\n`;
  }).join('');

  // 返回N8N格式的对象数组
  return [{
    json: {
      output: formattedText
    }
  }];
}

// 执行函数并返回结果
return processData();
